import React from "react";
import CurrencyFormat from "react-currency-format";

function myCurrencyFormat({ price }) {
  return (
    <CurrencyFormat
      returnText={(price) => ({ price })}
      decimalScale={2}
      value={price}
      displayType={"text"}
      thousandSeparator={true}
    />
  );
}

export default currencyFor;
